static void compute_indexmap(int d1, int d2, int d3)
{
  int i, j, k, kk, kk2, jj, kj2, ii;
  double ap;

  ap = -4.0 * ALPHA * PI * PI;

  #pragma omp target teams loop collapse(3) is_device_ptr(d_twiddle)
  for (k = 0; k < d3; k++) {
    for (j = 0; j < d2; j++) {
      for (i = 0; i < d1; i++) {
        kk = ((k + NZ/2) % NZ) - NZ/2;
        kk2 = kk*kk;
        jj = ((j + NY/2) % NY) - NY/2;
        kj2 = jj*jj + kk2;
        ii = ((i + NX/2) % NX) - NX/2;

        d_twiddle[k*d2*(d1+1) + j*(d1+1) + i] = exp(ap * (double)(ii*ii+kj2));
      }
    }
  }
}

