static void fft_init(int n)
{
  int m, nu, ku, i, j, ln;
  double t, ti;

  nu = n;
  m = ilog2(n);

  #pragma omp target is_device_ptr(d_u_real, d_u_imag) firstprivate(m)
  {
    d_u_real[0] = m;
    d_u_imag[0] = 0.0;
  }
  ku = 2;
  ln = 1;

  for (j = 1; j <= m; j++) {
    t = PI / ln;

    #pragma omp target teams loop is_device_ptr(d_u_real, d_u_imag) firstprivate(t, ku, ln)
    for (i = 0; i <= ln - 1; i++) {
      ti = i * t;

      d_u_real[i+ku-1] = (double)cos(ti);
      d_u_imag[i+ku-1] = (double)sin(ti);
    }

    ku = ku + ln;
    ln = 2 * ln;
 }
}

