  randlc_local(&start, an);
  an = ipow46(A, 2*NX*NY);

  double starts[MAXDIM];
  starts[0] = start;
  for (k = 1; k < d3; k++) {
    randlc_local(&start, an);
    starts[k] = start;
  }

  for (k = 0; k < d3; k++) {
    double x0 = starts[k];
    for (j = 0; j < d2; j++) {

      const double r23 = 1.1920928955078125e-07;
      const double r46 = r23 * r23;
      const double t23 = 8.388608e+06;
      const double t46 = t23 * t23;

      double t1, t2, t3, t4, a1, a2, x1, x2, z;

      t1 = r23*A;
      a1 = (int)t1;
      a2 = A - t23*a1;

      for (int i = 0; i < 2*d1; i++) {
        t1 = r23 * x0;
        x1 = (int)t1;
        x2 = x0 - t23*x1;
        t1 = a1 * x2 + a2 * x1;
        t2 = (int) (r23 * t1);
        z = t1 - t23 * t2;
        t3 = t23 * z + a2 * x2;
        t4 = (int) (r46 * t3) ;
        x0 = t3 - t46 * t4;

        if(i%2 == 0)
            host_u1_real[k*d2*(d1+1) + j*(d1+1) + i/2] = r46*x0;
        else
            host_u1_imag[k*d2*(d1+1) + j*(d1+1) + i/2] = r46*x0;
