    }
  }

  omp_target_memcpy(d_u1_real, host_u1_real, d3 * d2 * (d1 + 1) * sizeof(double), 0, 0,
                    target_dev, omp_get_initial_device());
  omp_target_memcpy(d_u1_imag, host_u1_imag, d3 * d2 * (d1 + 1) * sizeof(double), 0, 0,
                    target_dev, omp_get_initial_device());

