#pragma omp declare target
static double ipow46(double a, int exponent)
{
  double result, dummy, q, r;
  int n, n2;

  result = 1;
  if (exponent == 0) return result;
  q = a;
  r = 1;
  n = exponent;

  while (n > 1) {
    n2 = n / 2;
    if (n2 * 2 == n) {
      dummy = randlc_local(&q, q);
      n = n2;
    } else {
      dummy = randlc_local(&r, q);
      n = n-1;
    }
  }
  dummy = randlc_local(&r, q);
  result = r;
  return result;
}
#pragma omp end declare target

