static void setup(int *n1, int *n2, int *n3)
{
  int k, j;

  int ax, mi[MAXLEVEL+1][3];
  int ng[MAXLEVEL+1][3];

  ng[lt][0] = nx[lt];
  ng[lt][1] = ny[lt];
  ng[lt][2] = nz[lt];
  for (k = lt-1; k >= 1; k--) {
    for (ax = 0; ax < 3; ax++) {
      ng[k][ax] = ng[k+1][ax]/2;
    }
  }
  for (k = lt; k >= 1; k--) {
    nx[k] = ng[k][0];
    ny[k] = ng[k][1];
    nz[k] = ng[k][2];
  }

  for (k = lt; k >= 1; k--) {
    for (ax = 0; ax < 3; ax++) {
      mi[k][ax] = 2 + ng[k][ax];
    }

    m1[k] = mi[k][0];
    m2[k] = mi[k][1];
    m3[k] = mi[k][2];
  }

  k = lt;
  is1 = 2 + ng[k][0] - ng[lt][0];
  ie1 = 1 + ng[k][0];
  *n1 = 3 + ie1 - is1;
  is2 = 2 + ng[k][1] - ng[lt][1];
  ie2 = 1 + ng[k][1];
  *n2 = 3 + ie2 - is2;
  is3 = 2 + ng[k][2] - ng[lt][2];
  ie3 = 1 + ng[k][2];
  *n3 = 3 + ie3 - is3;

  ir[lt] = 0;
  for (j = lt-1; j >= 1; j--) {
    ir[j] = ir[j+1]+ONE*m1[j+1]*m2[j+1]*m3[j+1];
  }

  if (debug_vec[1] >= 1) {
    printf(" in setup, \n");
    printf(" k  lt  nx  ny  nz  n1  n2  n3 is1 is2 is3 ie1 ie2 ie3\n");
    printf("%4d%4d%4d%4d%4d%4d%4d%4d%4d%4d%4d%4d%4d%4d\n", 
        k,lt,ng[k][0],ng[k][1],ng[k][2],*n1,*n2,*n3,is1,is2,is3,ie1,ie2,ie3);
  }
}

static void mg3P(double u[], double v[], double r[],
                 double a[4], double c[4], int n1, int n2, int n3)
{
  int j, k;

  for (k = lt; k >= lb+1; k--) {
    j = k - 1;
    rprj3(&r[ir[k]], m1[k], m2[k], m3[k], 
          &r[ir[j]], m1[j], m2[j], m3[j], k);
  }

  k = lb;

  zero3(&u[ir[k]], m1[k], m2[k], m3[k]);
  psinv(&r[ir[k]], &u[ir[k]], m1[k], m2[k], m3[k], c, k);

  for (k = lb+1; k <= lt-1; k++) {
    j = k - 1;

    zero3(&u[ir[k]], m1[k], m2[k], m3[k]);
    interp(&u[ir[j]], m1[j], m2[j], m3[j], &u[ir[k]], m1[k], m2[k], m3[k], k);

    resid(&u[ir[k]], &r[ir[k]], &r[ir[k]], m1[k], m2[k], m3[k], a, k);

    psinv(&r[ir[k]], &u[ir[k]], m1[k], m2[k], m3[k], c, k);
  }

  j = lt - 1;
  k = lt;
  interp(&u[ir[j]], m1[j], m2[j], m3[j], u, n1, n2, n3, k);
  resid(u, v, r, n1, n2, n3, a, k);
  psinv(r, u, n1, n2, n3, c, k);
}

static void psinv(double * __restrict__ orr, double * __restrict__ ou, int n1, int n2, int n3, double c[4], int k)
{

  int i3, i2, i1;
  double c0, c1, c2;

  double * __restrict__ r1, * __restrict__ r2;

  c0 = c[0];
  c1 = c[1];
  c2 = c[2];

r1 = (double*)malloc(n3*n2*n1*sizeof(double));
r2 = (double*)malloc(n3*n2*n1*sizeof(double));

  if (timeron) timer_start(T_psinv);
  for (i3 = 1; i3 < n3-1; i3++) {
    for (i2 = 1; i2 < n2-1; i2++) {
      for (i1 = 0; i1 < n1; i1++) {

        I3D(r1, n1, n2, i3, i2, i1) = I3D(orr, n1, n2, i3, i2-1, i1) + I3D(orr, n1, n2, i3, i2+1, i1)
               + I3D(orr, n1, n2, i3-1, i2, i1) + I3D(orr, n1, n2, i3+1, i2, i1);
        I3D(r2, n1, n2, i3, i2, i1) = I3D(orr, n1, n2, i3-1, i2-1, i1) + I3D(orr, n1, n2, i3-1, i2+1, i1)
               + I3D(orr, n1, n2, i3+1, i2-1, i1) + I3D(orr, n1, n2, i3+1, i2+1, i1);

      }
    }
  }
  for (i3 = 1; i3 < n3-1; i3++) {
    for (i2 = 1; i2 < n2-1; i2++) {
      for (i1 = 1; i1 < n1-1; i1++) {

        I3D(ou, n1, n2, i3, i2, i1) = I3D(ou, n1, n2, i3, i2, i1)
                                    + c0 * I3D(orr, n1, n2, i3, i2, i1)
                                    + c1 * ( I3D(orr, n1, n2, i3, i2, i1-1)
                                             + I3D(orr, n1, n2, i3, i2, i1+1)
											 + I3D(r1, n1, n2, i3, i2, i1) )
									+ c2 * ( I3D(r2, n1, n2, i3, i2, i1)
									         + I3D(r1, n1, n2, i3, i2, i1-1)
											 + I3D(r1, n1, n2, i3, i2, i1+1));

      }
    }
  }
  if (timeron) timer_stop(T_psinv);

 free(r1);
 free(r2);

  comm3(ou, n1, n2, n3, k);

  if (debug_vec[0] >= 1) {

    rep_nrm(ou, n1, n2, n3, "   psinv", k);
  }

  if (debug_vec[3] >= k) {

    showall(ou, n1, n2, n3);
  }
}

