      jg[3][i][1] = is3 - 2 + j3[i1][1];
      i1 = i1-1;
    } else {
      jg[0][i][1] = 0;
      jg[1][i][1] = 0;
      jg[2][i][1] = 0;
      jg[3][i][1] = 0;
    }

    best = 1.0;
    if (best > ten[i0][0]) {
      jg[0][i][0] = 0;
      jg[1][i][0] = is1 - 2 + j1[i0][0];
      jg[2][i][0] = is2 - 2 + j2[i0][0];
      jg[3][i][0] = is3 - 2 + j3[i0][0];
      i0 = i0-1;
    } else {
      jg[0][i][0] = 0;
      jg[1][i][0] = 0;
      jg[2][i][0] = 0;
      jg[3][i][0] = 0;
    }

  }

  m1 = 0;
  m0 = 0;

  for (i3 = 0; i3 < n3; i3++) {
    for (i2 = 0; i2 < n2; i2++) {
      for (i1 = 0; i1 < n1; i1++) {

        I3D(oz, n1, n2, i3, i2, i1) = 0.0;
      }
    }
  }

  for (i = mm-1; i >= m0; i--) {

	i3 = jg[3][i][0];
	i2 = jg[2][i][0];
	i1 = jg[1][i][0];
    I3D(oz, n1, n2, i3, i2, i1) = -1.0;
  }

  for (i = mm-1; i >= m1; i--) {

	i3 = jg[3][i][1];
	i2 = jg[2][i][1];
	i1 = jg[1][i][1];
    I3D(oz, n1, n2, i3, i2, i1) = +1.0;
  }

  comm3(oz, n1, n2, n3, k);

}

